import { Box, Button, Grid, Paper, TextField } from '@material-ui/core'
import axios from 'axios';
import React, { useState } from 'react'
import UsersComponent from './UsersComponent';
import Fade from 'react-reveal/Fade';

export default function BaseComponent() {
    const [searchInput, setSearchInput] = useState('');
    const [gitUsers, setGitUsers] = useState({});
    const [loading, setLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    
    const handleChange = (e) => {
        setSearchInput(e.target.value)
      }
  
      const handleSubmit = async(e) => {
        e.preventDefault();
        setLoading(true)
        try {
            const data = await axios.get(`https://api.github.com/users/${searchInput}`);
            setGitUsers(data.data);
            setLoading(false)
            setErrorMessage('')            
            console.log(gitUsers)
        }catch(err){
            console.log(err.message)
            setErrorMessage(err.message)
            setLoading(false)
        }
     }

    return (
        <Fade bottom cascade>
            <Box className="baseComp">
                <Paper style={{height: '40%'}}>
                        <Grid container style={{marginTop: '4rem'}}>
                            <form onSubmit={handleSubmit}>
                                <Grid item md={8} xs={8}> 
                                    <Box style={{display: 'flex', justifyContent: 'center'}}>
                                        <TextField 
                                            variant="outlined" 
                                            label="get a git user information" 
                                            placeholder="your git username"
                                            fullWidth
                                            required
                                            onChange={handleChange}
                                            style={{width: '100%'}}
                                        />
                                    </Box>
                                </Grid>
                                <Grid item md={4} xs={4}>
                                    <Button type="submit" variant="contained" style={{ height: '3.4rem',      background: '#2cc5b4', color: 'white'}}   disabled={searchInput === ''}>
                                                Search
                                    </Button>
                                </Grid>
                            </form>
                        </Grid> 
                    <UsersComponent 
                        gitUsers={gitUsers} 
                        loading={loading} 
                        errorMessage={errorMessage}
                        searchInput={searchInput}
                        />
                </Paper>
            </Box>
        </Fade>
    )
}
