import { Box, Button, Grid, Paper, TextField } from '@material-ui/core'
import axios from 'axios';
import React, { useState } from 'react'
import UsersComponent from './UsersComponent';

export default function BaseComponent() {
    const [searchInput, setSearchInput] = useState('');
    const [gitUsers, setGitUsers] = useState({});
    const [loading, setLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('')
    
    const handleChange = (e) => {
        setSearchInput(e.target.value)
      }
  
      const handleSubmit = async(e) => {
        e.preventDefault();
        setLoading()
        try {
            const data = await axios.get(`https://api.github.com/users/${searchInput}`);
            setGitUsers(data.data);
            setLoading(false)
            setErrorMessage('')            
            console.log(gitUsers)
        }catch(err){
            console.log(err.message)
            setErrorMessage(err.message)
        }
     }

    return (
        <Box className="baseComp">
            <Paper style={{height: '40%'}}>
                    <Grid container style={{marginTop: '2rem'}}>
                        <form onSubmit={handleSubmit}>
                            <Grid item md={12} xs={12}> 
                               <Box style={{display: 'flex', justifyContent: 'center'}}>
                                   <Box>
                                        <TextField 
                                            variant="outlined" 
                                            label="get a git user information" 
                                            placeholder="search"
                                            required
                                            onChange={handleChange}
                                        />
                                    </Box>
                                    <Box>
                                        <Button type="submit" variant="contained" style={{ height: '3.4rem'}}>
                                            Search
                                        </Button>
                                    </Box>
                               </Box>
                            </Grid>
                        </form>
                    </Grid> 
                   <UsersComponent 
                      gitUsers={gitUsers} 
                      loading={loading} 
                      errorMessage={errorMessage}
                      />
            </Paper>
        </Box>
    )
}
